mapper.select:
SELECT

FROM t_pay_alipay_notify AS t
WHERE 1 = 1 

 	and t.status = #{c_status}


 	and t.time >= #{c_startTime}  and t.time <= #{c_endTime}

SELECT

FROM t_pay_alipay_notify AS t
WHERE 1=1 and t.id = #{id}

SELECT

FROM t_pay_alipay_notify AS t
WHERE 1=1 

 	and t.status =#{c_status}


 	limit 0,#{c_limit}
